mapper.select:
SELECT ROW_NUMBER() OVER (ORDER BY A.VLT_DT DESC, A.VLT_ATIME DESC) AS NO /*순번*/ , CASE WHEN B.GD_CD LIKE 'RC%' THEN '렌터카' ELSE '리스' END AS GD_NM /*상품명*/ , B.CS_NM /*고객명*/ , A.VHCL_NO /*차량번호*/ , A.VLT_DT /*위반일자*/ , A.VLT_ATIME /*위반시각*/ , C.NTCDOC_SEND_PLC_NM AS SEND_PLC_NM /*발송처명*/ , C.NTCDOC_SEND_PLC_DEPT_NM AS SEND_PLC_DEPT_NM /*발송처부서명*/ , (SELECT X.CD_NM FROM BIZ_COMN_CD_DTL X WHERE X.COMN_CD_ENG_NM = 'VLT_KIND_CD' AND X.CD_VAL = A.VLT_KIND_CD) AS VLT_KIND_NM /*위반종류명*/ , A.VLT_CTS /*위반내용*/ , A.VLT_PNT /*위반장소*/ , A.FINE_AMT /*범칙금금액*/ , A.PYMT_DDAY_DT /*납부기한일자*/ , A.RCPT_DT /*접수일자*/ , A.PYMT_DT /*납입일자*/ , CASE WHEN A.DRVR_CHGE_DT != '' THEN 'Y' ELSE 'N' END AS DRVR_CHGE_YN /*운전자변경여부*/ , A.DRVR_CHGE_DT /*운전자변경일*/ , (SELECT X.CD_NM FROM BIZ_COMN_CD_DTL X WHERE X.COMN_CD_ENG_NM = 'LOAN_STAT_CD' AND X.CD_VAL = B.LOAN_STAT_CD) AS LOAN_STAT_NM /*대출상태명*/ FROM BIZ_FINE_MAS A , BIZ_LOAN_MAS B , BIZ_FINE_SEND_PLC_STD_MNGE C WHERE A.VHCL_NO = B.VHCL_NO AND A.VLT_DT >= B.LOAN_DT AND (A.VLT_DT < B.TMNT_DT OR B.TMNT_DT = '') AND A.SEND_PLC_CD = C.SEND_PLD_CD AND A.SEND_PLC_SEQ = C.SEND_PLC_SEQ AND A.VLT_DT BETWEEN #{inVltDtStrt} AND #{inVltDtEnd} /*검색조건-위반일자*/
AND A.VLT_KIND_CD = #{inVltKindCd}
AND A.SEND_PLC_CD = #{inSendPlcCd}
AND B.GD_CD NOT LIKE 'RC%'
AND B.GD_CD LIKE 'RC%'
AND B.CS_NM = #{inCsNm}
AND A.VHCL_NO = #{inVhclNo}
ORDER BY NO
